import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '',
    description: '',
    isin: '',
    expirationCode: '',
    parentTicker: '',
    fullName: '',
    segment: '',
    maturityDate: '',
    firstTradingDate: '',
    lastTraadingDate: '',
    initialMarginAmount: 0,
    percentageMargin: 0,
    lotSize: 0,
    contractMultiplier: 0,
    tradingCurrency: '',
    underlyingType: '',
    underlyingId: 0,
    settlementMethod: '',
    instrumentStatus: false,
    tickSize: 0,
    tickValue: 0,
    depositType: '',
  });

  const [settlementMethods, setSettlementMethods] = useState<string[]>([]);
  const [depositTypes, setDepositTypes] = useState<string[]>([]);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>([]);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([]);
   const [tickMode, setTickMode] = useState<'value' | 'multiplier'>('value');

  useEffect(() => {
    async function fetchEnums() {
      const [settlementRes, depositRes, typeRes] = await Promise.all([
        axios.get<string[]>('/api/enums/settlement-methods'),
        axios.get<string[]>('/api/enums/deposit-types'),
        axios.get<string[]>('/api/underlyings/types'),
      ]);
      setSettlementMethods(settlementRes.data);
      setDepositTypes(depositRes.data);
      setUnderlyingTypes(typeRes.data);
    }
    fetchEnums();
  }, []);

  useEffect(() => {
    if (form.underlyingType) {
      axios.get<{ id: number; identifier: string }[]>(`/api/underlying-assets?type=${form.underlyingType}`)
        .then(res => setUnderlyingAssets(res.data))
        .catch(() => setUnderlyingAssets([]));
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    } else {
      setUnderlyingAssets([]);
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    }
  }, [form.underlyingType]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await axios.post('/api/futures', form);
      alert('Future créé avec succès');
    } catch (err) {
      alert('Erreur lors de la création');
      console.error(err);
    }
  };

  return (
  <form onSubmit={handleSubmit}>
  <div><label>Symbol</label><input name="symbol" value={form.symbol} onChange={handleChange} required /></div>
  <div><label>Description</label><input name="description" value={form.description} onChange={handleChange} /></div>
  <div><label>ISIN</label><input name="isin" value={form.isin} onChange={handleChange} /></div>
  <div><label>Expiration Code</label><input name="expirationCode" value={form.expirationCode} onChange={handleChange} /></div>
  <div><label>Parent Ticker</label><input name="parentTicker" value={form.parentTicker} onChange={handleChange} /></div>
  <div><label>Full Name</label><input name="fullName" value={form.fullName} onChange={handleChange} /></div>
  <div><label>Segment</label><input name="segment" value={form.segment} onChange={handleChange} /></div>
  <div><label>Maturity Date</label><input type="date" name="maturityDate" value={form.maturityDate} onChange={handleChange} /></div>
  <div><label>First Trading Date</label><input type="date" name="firstTradingDate" value={form.firstTradingDate} onChange={handleChange} /></div>
  <div><label>Last Trading Date</label><input type="date" name="lastTraadingDate" value={form.lastTraadingDate} onChange={handleChange} /></div>
  <div><label>Initial Margin Amount</label><input type="number" name="initialMarginAmount" value={form.initialMarginAmount} onChange={handleChange} /></div>
  <div><label>Percentage Margin</label><input type="number" name="percentageMargin" value={form.percentageMargin} onChange={handleChange} /></div>
  <div><label>Lot Size</label><input type="number" name="lotSize" value={form.lotSize} onChange={handleChange} /></div>
  <div><label>Trading Currency</label><input name="tradingCurrency" value={form.tradingCurrency} onChange={handleChange} /></div>

  <div>
    <label>Underlying Type</label>
    <select name="underlyingType" value={form.underlyingType} onChange={handleChange} required>
      <option value="">Sélectionnez</option>
      {underlyingTypes.map(type => (
        <option key={type} value={type}>{type}</option>
      ))}
    </select>
  </div>

  <div>
    <label>Underlying Asset</label>
    <select name="underlyingId" value={form.underlyingId} onChange={handleChange} required disabled={!form.underlyingType}>
      <option value={0}>Sélectionnez</option>
      {underlyingAssets.map(asset => (
        <option key={asset.id} value={asset.id}>{asset.identifier}</option>
      ))}
    </select>
  </div>

  <div>
    <label>Settlement Method</label>
    <select name="settlementMethod" value={form.settlementMethod} onChange={handleChange} required>
      <option value="">Sélectionnez</option>
      {settlementMethods.map(method => (
        <option key={method} value={method}>{method}</option>
      ))}
    </select>
  </div>

  <div>
    <label>Instrument Actif</label>
    <input type="checkbox" name="instrumentStatus" checked={form.instrumentStatus} onChange={handleChange} />
  </div>

  <div>
    <label>Deposit Type</label>
    <select name="depositType" value={form.depositType} onChange={handleChange} required>
      <option value="">Sélectionnez</option>
      {depositTypes.map(type => (
        <option key={type} value={type}>{type}</option>
      ))}
    </select>
  </div>


<div>
  <label>Mode Tick :</label>
  <select value={tickMode} onChange={(e) => setTickMode(e.target.value as 'value' | 'multiplier')}>
    <option value="value">Saisir Tick Value</option>
    <option value="multiplier">Saisir Contract Multiplier</option>
  </select>
</div>
<div>
  <label>Tick Size</label>
  <input
  type="text"
  name="tickSize"
  value={form.tickSize === 0 ? '' : form.tickSize.toString()}
  onChange={(e) => {
    const val = e.target.value;
    // Autoriser seulement chiffres et un seul point
    if (/^\d*\.?\d*$/.test(val)) {
      setForm(prev => ({
        ...prev,
        tickSize: val === '' ? 0 : parseFloat(val),
      }));
    }
  }}
/>

</div>


{tickMode === 'value' ? (
  <div>
    <label>Tick Value</label>
    <input type="number" name="tickValue" value={form.tickValue} onChange={(e) => {
      const value = parseFloat(e.target.value);
      setForm(prev => ({
        ...prev,
        tickValue: value,
        contractMultiplier: form.tickSize > 0 ? value / form.tickSize : 0,
      }));
    }} />
  </div>
) : (
  <div>
    <label>Contract Multiplier</label>
    <input type="number" name="contractMultiplier" value={form.contractMultiplier} onChange={(e) => {
      const value = parseFloat(e.target.value);
      setForm(prev => ({
        ...prev,
        contractMultiplier: value,
        tickValue: value * form.tickSize,
      }));
    }} />
  </div>
)}

  <button type="submit">Créer Future</button>
</form>

  );
};

export default FutureCreationForm;
