import React from 'react';
import { CheckCircle, XCircle, AlertCircle, Info } from 'lucide-react';

export type NotificationType = 'success' | 'error' | 'warning' | 'info';

interface NotificationToastProps {
  type: NotificationType;
  title: string;
  message?: string;
  duration?: number;
  onClose?: () => void;
}

const notificationStyles = {
  success: {
    icon: CheckCircle,
    bgColor: 'bg-green-50',
    borderColor: 'border-green-200',
    textColor: 'text-green-800',
    iconColor: 'text-green-600',
  },
  error: {
    icon: XCircle,
    bgColor: 'bg-red-50',
    borderColor: 'border-red-200',
    textColor: 'text-red-800',
    iconColor: 'text-red-600',
  },
  warning: {
    icon: AlertCircle,
    bgColor: 'bg-yellow-50',
    borderColor: 'border-yellow-200',
    textColor: 'text-yellow-800',
    iconColor: 'text-yellow-600',
  },
  info: {
    icon: Info,
    bgColor: 'bg-blue-50',
    borderColor: 'border-blue-200',
    textColor: 'text-blue-800',
    iconColor: 'text-blue-600',
  },
};

export const NotificationToast: React.FC<NotificationToastProps> = ({
  type,
  title,
  message,
  duration = 5000,
  onClose,
}) => {
  const styles = notificationStyles[type];
  const IconComponent = styles.icon;

  React.useEffect(() => {
    if (duration > 0) {
      const timer = setTimeout(() => {
        onClose?.();
      }, duration);
      return () => clearTimeout(timer);
    }
  }, [duration, onClose]);

  return (
    <div
      className={`fixed top-4 right-4 z-50 max-w-sm w-full ${styles.bgColor} border ${styles.borderColor} rounded-lg shadow-lg p-4 transform transition-all duration-300 ease-out`}
      role="alert"
      aria-live="assertive"
    >
      <div className="flex items-start space-x-3">
        <IconComponent className={`w-5 h-5 mt-0.5 ${styles.iconColor}`} />
        <div className="flex-1 min-w-0">
          <h4 className={`text-sm font-semibold ${styles.textColor}`}>
            {title}
          </h4>
          {message && (
            <p className={`text-sm mt-1 ${styles.textColor} opacity-90`}>
              {message}
            </p>
          )}
        </div>
        {onClose && (
          <button
            onClick={onClose}
            className={`ml-2 p-1 rounded-full hover:bg-white/50 transition-colors duration-200 ${styles.textColor} opacity-70 hover:opacity-100`}
            aria-label="Fermer la notification"
          >
            <XCircle className="w-4 h-4" />
          </button>
        )}
      </div>
    </div>
  );
};
