import React, { useState, useEffect } from 'react';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '',
    description: '',
    isin: '',
    expirationCode: '',
    parentTicker: '',
    fullName: '',
    segment: '',
    maturityDate: '',
    firstTradingDate: '',
    lastTraadingDate: '',
    initialMarginAmount: 0,
    percentageMargin: 0,
    lotSize: 0,
    contractMultiplier: 0,
    tradingCurrency: '',
    underlyingType: '',
    underlyingId: 0,
    settlementMethod: '',
    instrumentStatus: false,
    tickSize: 0,
    tickValue: 0,
    depositType: '',
  });

  const [settlementMethods, setSettlementMethods] = useState<string[]>(['CASH', 'PHYSICAL']);
  const [depositTypes, setDepositTypes] = useState<string[]>(['RATE', 'AMOUNT']);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>(['STOCK', 'INDEX', 'COMMODITY']);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([
    { id: 1, identifier: 'AAPL' },
    { id: 2, identifier: 'GOOGL' },
    { id: 3, identifier: 'MSFT' }
  ]);

  const [editMode, setEditMode] = useState<'tickValue' | 'contractMultiplier'>('tickValue');
  const [tickSizeStr, setTickSizeStr] = useState('');
  const [tickValueInput, setTickValueInput] = useState(0);
  const [contractMultiplierInput, setContractMultiplierInput] = useState(0);

  useEffect(() => {
    setTickSizeStr(form.tickSize > 0 ? form.tickSize.toString() : '');
  }, [form.tickSize]);

  useEffect(() => {
    const tickSize = parseTickSize(tickSizeStr);
    if (editMode === 'tickValue') {
      const contractMultiplierCalc = tickSize > 0 ? tickValueInput / tickSize : 0;

      setForm(prev => {
        if (
          prev.tickSize !== tickSize ||
          prev.tickValue !== tickValueInput ||
          prev.contractMultiplier !== contractMultiplierCalc
        ) {
          return {
            ...prev,
            tickSize,
            tickValue: tickValueInput,
            contractMultiplier: contractMultiplierCalc,
          };
        }
        return prev;
      });

      setContractMultiplierInput(contractMultiplierCalc);
    } else {
      const tickValueCalc = contractMultiplierInput * tickSize;

      setForm(prev => {
        if (
          prev.tickSize !== tickSize ||
          prev.contractMultiplier !== contractMultiplierInput ||
          prev.tickValue !== tickValueCalc
        ) {
          return {
            ...prev,
            tickSize,
            contractMultiplier: contractMultiplierInput,
            tickValue: tickValueCalc,
          };
        }
        return prev;
      });

      setTickValueInput(tickValueCalc);
    }
  }, [tickSizeStr, editMode, tickValueInput, contractMultiplierInput]);

  const parseTickSize = (str: string): number => {
    if (!str) return 0;
    const val = parseFloat(str);
    return isNaN(val) ? 0 : val;
  };

  const isRate = form.depositType === 'RATE';
  const isAmount = form.depositType === 'AMOUNT';

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;
    setForm(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handlePercentageMarginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value) || 0;
    setForm(prev => ({ ...prev, percentageMargin: val, initialMarginAmount: prev.lotSize * val }));
  };

  const handleInitialMarginAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value) || 0;
    setForm(prev => ({ ...prev, initialMarginAmount: val, percentageMargin: prev.lotSize > 0 ? val / prev.lotSize : 0 }));
  };

  const handleTickValueChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value) || 0;
    setTickValueInput(val);
  };

  const handleContractMultiplierChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value) || 0;
    setContractMultiplierInput(val);
  };

  const handleTickSizeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTickSizeStr(e.target.value);
  };

  const handleModeChange = (mode: 'tickValue' | 'contractMultiplier') => {
    setEditMode(mode);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    alert('Future créé avec succès');
    console.log('Form data:', form);
  };

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b px-6 py-3">
        <h1 className="text-2xl font-bold text-gray-900">Création de Future</h1>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-hidden">
        <div className="h-full flex">
          {/* Left Column */}
          <div className="w-1/2 p-4 overflow-y-auto border-r">
            {/* General Info */}
            <div className="bg-white rounded-lg shadow-sm p-4 mb-4">
              <h2 className="text-lg font-semibold mb-3 text-blue-700 border-b pb-2">Informations Générales</h2>
              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="block text-xs font-medium mb-1">Symbol*</label>
                  <input name="symbol" value={form.symbol} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" required />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">ISIN</label>
                  <input name="isin" value={form.isin} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
                <div className="col-span-2">
                  <label className="block text-xs font-medium mb-1">Description</label>
                  <input name="description" value={form.description} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Expiration Code</label>
                  <input name="expirationCode" value={form.expirationCode} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Parent Ticker</label>
                  <input name="parentTicker" value={form.parentTicker} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
                <div className="col-span-2">
                  <label className="block text-xs font-medium mb-1">Full Name</label>
                  <input name="fullName" value={form.fullName} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Segment</label>
                  <input name="segment" value={form.segment} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Trading Currency</label>
                  <input name="tradingCurrency" value={form.tradingCurrency} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-blue-500" />
                </div>
              </div>
            </div>

            {/* Dates */}
            <div className="bg-white rounded-lg shadow-sm p-4 mb-4">
              <h2 className="text-lg font-semibold mb-3 text-green-700 border-b pb-2">Dates</h2>
              <div className="grid grid-cols-3 gap-3">
                <div>
                  <label className="block text-xs font-medium mb-1">First Trading</label>
                  <input type="date" name="firstTradingDate" value={form.firstTradingDate} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-green-500" />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Last Trading</label>
                  <input type="date" name="lastTraadingDate" value={form.lastTraadingDate} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-green-500" />
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Maturity</label>
                  <input type="date" name="maturityDate" value={form.maturityDate} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-green-500" />
                </div>
              </div>
            </div>

            {/* Underlying */}
            <div className="bg-white rounded-lg shadow-sm p-4">
              <h2 className="text-lg font-semibold mb-3 text-purple-700 border-b pb-2">Underlying Asset</h2>
              <div className="space-y-3">
                <div>
                  <label className="block text-xs font-medium mb-1">Underlying Type</label>
                  <select name="underlyingType" value={form.underlyingType} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-purple-500">
                    <option value="">Sélectionner...</option>
                    {underlyingTypes.map(type => (<option key={type} value={type}>{type}</option>))}
                  </select>
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Underlying Asset</label>
                  <select name="underlyingId" value={form.underlyingId} onChange={handleChange} disabled={!form.underlyingType} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-purple-500 disabled:bg-gray-100">
                    <option value={0}>Sélectionner...</option>
                    {underlyingAssets.map(asset => (<option key={asset.id} value={asset.id}>{asset.identifier}</option>))}
                  </select>
                </div>
              </div>
            </div>
          </div>

          {/* Right Column */}
          <div className="w-1/2 p-4 overflow-y-auto">
            {/* Margin & Lot */}
            <div className="bg-white rounded-lg shadow-sm p-4 mb-4">
              <h2 className="text-lg font-semibold mb-3 text-orange-700 border-b pb-2">Margin & Lot Size</h2>
              <div className="space-y-3">
                <div>
                  <label className="block text-xs font-medium mb-1">Deposit Type</label>
                  <select name="depositType" value={form.depositType} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-orange-500">
                    <option value="">Sélectionner...</option>
                    {depositTypes.map(type => (<option key={type} value={type}>{type}</option>))}
                  </select>
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Lot Size</label>
                  <input type="number" name="lotSize" value={form.lotSize} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-orange-500" />
                </div>
                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <label className="block text-xs font-medium mb-1">Initial Margin Amount</label>
                    <input type="number" name="initialMarginAmount" value={form.initialMarginAmount} onChange={handleInitialMarginAmountChange} readOnly={!isAmount} className={`w-full p-2 text-sm border rounded focus:ring-2 focus:ring-orange-500 ${!isAmount ? 'bg-gray-100' : ''}`} />
                  </div>
                  <div>
                    <label className="block text-xs font-medium mb-1">Percentage Margin</label>
                    <input type="number" name="percentageMargin" value={form.percentageMargin} onChange={handlePercentageMarginChange} readOnly={!isRate} className={`w-full p-2 text-sm border rounded focus:ring-2 focus:ring-orange-500 ${!isRate ? 'bg-gray-100' : ''}`} />
                  </div>
                </div>
              </div>
            </div>

            {/* Tick Configuration */}
            <div className="bg-white rounded-lg shadow-sm p-4 mb-4">
              <h2 className="text-lg font-semibold mb-3 text-red-700 border-b pb-2">Configuration Tick</h2>
              <div className="space-y-3">
                <div className="flex gap-2">
                  <button type="button" onClick={() => handleModeChange('tickValue')} className={`flex-1 py-1 px-3 text-xs rounded transition ${editMode === 'tickValue' ? 'bg-red-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}>
                    Tick Value
                  </button>
                  <button type="button" onClick={() => handleModeChange('contractMultiplier')} className={`flex-1 py-1 px-3 text-xs rounded transition ${editMode === 'contractMultiplier' ? 'bg-red-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}>
                    Contract Multiplier
                  </button>
                </div>
                <div>
                  <label className="block text-xs font-medium mb-1">Tick Size</label>
                  <input type="text" name="tickSize" value={tickSizeStr} onChange={handleTickSizeChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-red-500" />
                </div>
                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <label className="block text-xs font-medium mb-1">
                      Tick Value {editMode !== 'tickValue' && <span className="text-gray-500">(calculé)</span>}
                    </label>
                    <input type="number" value={editMode === 'tickValue' ? tickValueInput : form.tickValue} onChange={handleTickValueChange} readOnly={editMode !== 'tickValue'} className={`w-full p-2 text-sm border rounded focus:ring-2 focus:ring-red-500 ${editMode !== 'tickValue' ? 'bg-gray-100' : ''}`} />
                  </div>
                  <div>
                    <label className="block text-xs font-medium mb-1">
                      Contract Multiplier {editMode !== 'contractMultiplier' && <span className="text-gray-500">(calculé)</span>}
                    </label>
                    <input type="number" value={editMode === 'contractMultiplier' ? contractMultiplierInput : form.contractMultiplier} onChange={handleContractMultiplierChange} readOnly={editMode !== 'contractMultiplier'} className={`w-full p-2 text-sm border rounded focus:ring-2 focus:ring-red-500 ${editMode !== 'contractMultiplier' ? 'bg-gray-100' : ''}`} />
                  </div>
                </div>
              </div>
            </div>

            {/* Settlement & Status */}
            <div className="bg-white rounded-lg shadow-sm p-4">
              <h2 className="text-lg font-semibold mb-3 text-indigo-700 border-b pb-2">Règlement & Statut</h2>
              <div className="space-y-3">
                <div>
                  <label className="block text-xs font-medium mb-1">Settlement Method</label>
                  <select name="settlementMethod" value={form.settlementMethod} onChange={handleChange} className="w-full p-2 text-sm border rounded focus:ring-2 focus:ring-indigo-500">
                    <option value="">Sélectionner...</option>
                    {settlementMethods.map(method => (<option key={method} value={method}>{method}</option>))}
                  </select>
                </div>
                <div className="flex items-center space-x-2">
                  <input type="checkbox" name="instrumentStatus" checked={form.instrumentStatus} onChange={handleChange} className="rounded border-gray-300 text-indigo-600 focus:ring-indigo-500" />
                  <label className="text-sm font-medium">Instrument Actif</label>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="bg-white border-t px-6 py-3 flex justify-end space-x-3">
        <button type="button" className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded hover:bg-gray-300 transition">
          Annuler
        </button>
        <button type="submit" onClick={handleSubmit} className="px-6 py-2 text-sm font-medium text-white bg-blue-600 rounded hover:bg-blue-700 transition">
          Créer Future
        </button>
      </div>
    </div>
  );
};

export default FutureCreationForm;
