                  </label>
                  <input
                    type="number"
                    name="lotSize"
                    value={form.lotSize}
                    onChange={handleChange}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
