import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '',
    description: '',
    isin: '',
    expirationCode: '',
    parentTicker: '',
    fullName: '',
    segment: '',
    maturityDate: '',
    firstTradingDate: '',
    lastTraadingDate: '',
    initialMarginAmount: 0,
    percentageMargin: 0,
    lotSize: 0,
    contractMultiplier: 0,
    tradingCurrency: '',
    underlyingType: '',
    underlyingId: 0,
    settlementMethod: '',
    instrumentStatus: false,
    tickSize: 0,
    tickValue: 0,
    depositType: '',
  });

  // Mode de saisie : on remplit soit tickValue, soit contractMultiplier
  const [tickMode, setTickMode] = useState<'value' | 'multiplier'>('value');

  const [settlementMethods, setSettlementMethods] = useState<string[]>([]);
  const [depositTypes, setDepositTypes] = useState<string[]>([]);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>([]);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([]);

  useEffect(() => {
    async function fetchEnums() {
      const [settlementRes, depositRes, typeRes] = await Promise.all([
        axios.get<string[]>('/api/enums/settlement-methods'),
        axios.get<string[]>('/api/enums/deposit-types'),
        axios.get<string[]>('/api/underlyings/types'),
      ]);
      setSettlementMethods(settlementRes.data);
      setDepositTypes(depositRes.data);
      setUnderlyingTypes(typeRes.data);
    }
    fetchEnums();
  }, []);

  useEffect(() => {
    if (form.underlyingType) {
      axios.get<{ id: number; identifier: string }[]>(`/api/underlying-assets?type=${form.underlyingType}`)
        .then(res => setUnderlyingAssets(res.data))
        .catch(() => setUnderlyingAssets([]));
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    } else {
      setUnderlyingAssets([]);
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    }
  }, [form.underlyingType]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

 const handleTickSizeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
  const val = e.target.value;
  if (val === '') {
    // Champ vidé => remettre tickSize à 0 ou null selon besoin
    setForm(prev => ({ ...prev, tickSize: 0 }));
  } else {
    const numVal = parseFloat(val);
    if (!isNaN(numVal) && numVal >= 0) {
      setForm(prev => {
        const newForm = {
          ...prev,
          tickSize: numVal,
        };

        if (tickMode === 'value') {
          newForm.contractMultiplier = numVal > 0 ? prev.tickValue / numVal : 0;
        } else {
          newForm.tickValue = numVal * prev.contractMultiplier;
        }
        return newForm;
      });
    }
  }
};


  const handleTickValueChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    if (!isNaN(val)) {
      setForm(prev => ({
        ...prev,
        tickValue: val,
        contractMultiplier: prev.tickSize > 0 ? val / prev.tickSize : 0,
      }));
    }
  };

  const handleContractMultiplierChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    if (!isNaN(val)) {
      setForm(prev => ({
        ...prev,
        contractMultiplier: val,
        tickValue: val * prev.tickSize,
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (form.tickSize <= 0) {
      alert('Veuillez saisir un Tick Size strictement supérieur à 0.');
      return;
    }

    try {
      await axios.post('/api/futures', form);
      alert('Future créé avec succès');
      // reset ou autre action après succès si nécessaire
    } catch (err) {
      alert('Erreur lors de la création');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 600, margin: 'auto', display: 'flex', flexDirection: 'column', gap: '1rem' }}>
      <div>
        <label>Symbol</label>
        <input name="symbol" value={form.symbol} onChange={handleChange} required />
      </div>
      <div>
        <label>Description</label>
        <input name="description" value={form.description} onChange={handleChange} />
      </div>
      <div>
        <label>ISIN</label>
        <input name="isin" value={form.isin} onChange={handleChange} />
      </div>
      <div>
        <label>Expiration Code</label>
        <input name="expirationCode" value={form.expirationCode} onChange={handleChange} />
      </div>
      <div>
        <label>Parent Ticker</label>
        <input name="parentTicker" value={form.parentTicker} onChange={handleChange} />
      </div>
      <div>
        <label>Full Name</label>
        <input name="fullName" value={form.fullName} onChange={handleChange} />
      </div>
      <div>
        <label>Segment</label>
        <input name="segment" value={form.segment} onChange={handleChange} />
      </div>
      <div>
        <label>Maturity Date</label>
        <input type="date" name="maturityDate" value={form.maturityDate} onChange={handleChange} />
      </div>
      <div>
        <label>First Trading Date</label>
        <input type="date" name="firstTradingDate" value={form.firstTradingDate} onChange={handleChange} />
      </div>
      <div>
        <label>Last Trading Date</label>
        <input type="date" name="lastTraadingDate" value={form.lastTraadingDate} onChange={handleChange} />
      </div>
      <div>
        <label>Initial Margin Amount</label>
        <input type="number" name="initialMarginAmount" value={form.initialMarginAmount} onChange={handleChange} />
      </div>
      <div>
        <label>Percentage Margin</label>
        <input type="number" name="percentageMargin" value={form.percentageMargin} onChange={handleChange} />
      </div>
      <div>
        <label>Lot Size</label>
        <input type="number" name="lotSize" value={form.lotSize} onChange={handleChange} />
      </div>
      <div>
        <label>Trading Currency</label>
        <input name="tradingCurrency" value={form.tradingCurrency} onChange={handleChange} />
      </div>

      <div>
        <label>Underlying Type</label>
        <select name="underlyingType" value={form.underlyingType} onChange={handleChange} required>
          <option value="">Sélectionnez</option>
          {underlyingTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>
      </div>

      <div>
        <label>Underlying Asset</label>
        <select name="underlyingId" value={form.underlyingId} onChange={handleChange} required disabled={!form.underlyingType}>
          <option value={0}>Sélectionnez</option>
          {underlyingAssets.map(asset => (
            <option key={asset.id} value={asset.id}>{asset.identifier}</option>
          ))}
        </select>
      </div>

      <div>
        <label>Settlement Method</label>
        <select name="settlementMethod" value={form.settlementMethod} onChange={handleChange} required>
          <option value="">Sélectionnez</option>
          {settlementMethods.map(method => (
            <option key={method} value={method}>{method}</option>
          ))}
        </select>
      </div>

      <div>
        <label>Instrument Actif</label>
        <input type="checkbox" name="instrumentStatus" checked={form.instrumentStatus} onChange={handleChange} />
      </div>

      <div>
        <label>Deposit Type</label>
        <select name="depositType" value={form.depositType} onChange={handleChange} required>
          <option value="">Sélectionnez</option>
          {depositTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>
      </div>

      <div>
        <label>Mode Tick :</label>
        <select value={tickMode} onChange={(e) => setTickMode(e.target.value as 'value' | 'multiplier')}>
          <option value="value">Saisir Tick Value</option>
          <option value="multiplier">Saisir Contract Multiplier</option>
        </select>
      </div>

     <div>
  <label>Tick Size</label>
  <input
    type="number"
    name="tickSize"
    value={form.tickSize}   // <-- ne pas mettre '' si 0, juste la valeur numérique
    onChange={handleTickSizeChange}
    min={0}
    step={0.0001}
  />
</div>


      {tickMode === 'value' ? (
        <div>
          <label>Tick Value</label>
          <input
            type="number"
            step="0.0001"
            min="0"
            name="tickValue"
            value={form.tickValue}
            onChange={handleTickValueChange}
          />
        </div>
      ) : (
        <div>
          <label>Contract Multiplier</label>
          <input
            type="number"
            step="0.0001"
            min="0"
            name="contractMultiplier"
            value={form.contractMultiplier}
            onChange={handleContractMultiplierChange}
          />
        </div>
      )}

      <button type="submit" style={{ marginTop: 20 }}>Créer Future</button>
    </form>
  );
};

export default FutureCreationForm;
