import React from 'react';
import { FutureFormData } from '../schemas/futureFormSchema';
import { FormField } from '../common/FormField';

interface DepositSectionProps {
  form: FutureFormData;
  errors: Record<string, string>;
  stringInputs: {
    percentageMargin: string;
    initialMarginAmount: string;
  };
  api: {
    depositTypes: string[];
    underlyingTypes: string[];
    underlyingAssets: Array<{ id: number; identifier: string }>;
  };
  onDepositTypeChange: (value: string) => void;
  onLotSizeChange: (value: number) => void;
  onPercentageMarginChange: (value: string) => void;
  onInitialMarginAmountChange: (value: string) => void;
  onUnderlyingTypeChange: (value: string) => void;
  onUnderlyingIdChange: (value: number) => void;
}

export const DepositSection: React.FC<DepositSectionProps> = ({
  form,
  errors,
  stringInputs,
  api,
  onDepositTypeChange,
  onLotSizeChange,
  onPercentageMarginChange,
  onInitialMarginAmountChange,
  onUnderlyingTypeChange,
  onUnderlyingIdChange,
}) => {
  return (
    <div className="space-y-2">
      {/* Deposit Configuration */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
        <FormField
          label="Type de dépôt"
          name="depositType"
          required
          error={errors.depositType}
          tooltip="Type de dépôt pour le calcul des marges"
        >
          <select
            value={form.depositType}
            onChange={(e) => onDepositTypeChange(e.target.value)}
            className="w-full px-3 py-1.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
            required
          >
            <option value="">Sélectionnez le type de dépôt</option>
            <option value="rate">Taux (%)</option>
            <option value="amount">Montant (€)</option>
          </select>
        </FormField>

        <FormField
          label="Taille de lot"
          name="lotSize"
          required
          error={errors.lotSize}
          tooltip="Nombre d'unités dans un lot"
        >
          <input
            type="number"
            value={form.lotSize}
            onChange={(e) => onLotSizeChange(Number(e.target.value))}
            className="w-full px-3 py-1.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
            min="1"
            step="1"
            required
          />
        </FormField>
      </div>

      {/* Margin Configuration */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
        <FormField
          label="Marge initiale (€)"
          name="initialMarginAmount"
          required
          error={errors.initialMarginAmount}
          tooltip="Montant de la marge initiale en euros"
        >
          <input
            type="text"
            value={stringInputs.initialMarginAmount}
            onChange={(e) => onInitialMarginAmountChange(e.target.value)}
            className="w-full px-3 py-1.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
            placeholder="0.00"
            required
          />
        </FormField>

        <FormField
          label="Pourcentage de marge (%)"
          name="percentageMargin"
          required
          error={errors.percentageMargin}
          tooltip="Pourcentage de marge requis"
        >
          <input
            type="text"
            value={stringInputs.percentageMargin}
            onChange={(e) => onPercentageMarginChange(e.target.value)}
            className="w-full px-3 py-1.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
            placeholder="0.00"
            required
          />
        </FormField>
      </div>

      {/* Underlying Assets */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
        <FormField
          label="Type de sous-jacent"
          name="underlyingType"
          required
          error={errors.underlyingType}
          tooltip="Type de l'actif sous-jacent"
        >
          <select
            value={form.underlyingType}
            onChange={(e) => onUnderlyingTypeChange(e.target.value)}
            className="w-full px-3 py-1.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
            required
          >
            <option value="">Sélectionnez le type de sous-jacent</option>
            {api.underlyingTypes.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </FormField>

        <FormField
          label="Sous-jacent"
          name="underlyingId"
          required
          error={errors.underlyingId}
          tooltip="Actif sous-jacent spécifique"
        >
          <select
            value={form.underlyingId}
            onChange={(e) => onUnderlyingIdChange(Number(e.target.value))}
            className="w-full px-3 py-1.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent transition duration-200"
            required
            disabled={!form.underlyingType}
          >
            <option value="">Sélectionnez un sous-jacent</option>
            {api.underlyingAssets
              .filter(asset => asset.type === form.underlyingType)
              .map(asset => (
                <option key={asset.id} value={asset.id}>
                  {asset.name} ({asset.symbol})
                </option>
              ))}
          </select>
        </FormField>
      </div>
    </div>
  );
};
