import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '',
    description: '',
    isin: '',
    expirationCode: '',
    parentTicker: '',
    fullName: '',
    segment: '',
    maturityDate: '',
    firstTradingDate: '',
    lastTraadingDate: '',
    initialMarginAmount: 0,
    percentageMargin: 0,
    lotSize: 0,
    contractMultiplier: 0,
    tradingCurrency: '',
    underlyingType: '',
    underlyingId: 0,
    settlementMethod: '',
    instrumentStatus: false,
    tickSize: 0,
    tickValue: 0,
    depositType: '',
  });

  // Mode de saisie : on remplit soit tickValue, soit contractMultiplier
  // Add editMode state (replace tickMode if needed)
  const [editMode, setEditMode] = useState<'tickValue' | 'contractMultiplier'>('tickValue');
  const [tickSizeStr, setTickSizeStr] = useState('');
  const [tickValueInput, setTickValueInput] = useState(0);
  const [contractMultiplierInput, setContractMultiplierInput] = useState(0);

  const [settlementMethods, setSettlementMethods] = useState<string[]>([]);
  const [depositTypes, setDepositTypes] = useState<string[]>([]);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>([]);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([]);

  useEffect(() => {
    async function fetchEnums() {
      const [settlementRes, depositRes, typeRes] = await Promise.all([
        axios.get<string[]>('/api/enums/settlement-methods'),
        axios.get<string[]>('/api/enums/deposit-types'),
        axios.get<string[]>('/api/underlyings/types'),
      ]);
      setSettlementMethods(settlementRes.data);
      setDepositTypes(depositRes.data);
      setUnderlyingTypes(typeRes.data);
    }
    fetchEnums();
  }, []);

  useEffect(() => {
    if (form.underlyingType) {
      axios.get<{ id: number; identifier: string }[]>(`/api/underlying-assets?type=${form.underlyingType}`)
        .then(res => setUnderlyingAssets(res.data))
        .catch(() => setUnderlyingAssets([]));
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    } else {
      setUnderlyingAssets([]);
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    }
  }, [form.underlyingType]);
useEffect(() => {
    setTickSizeStr(form.tickSize > 0 ? form.tickSize.toString() : '');
  }, [form.tickSize]);

// When tick size or mode changes, recalculate the derived value
useEffect(() => {
  const tickSize = parseTickSize(tickSizeStr);
  if (editMode === 'tickValue') {
    setForm(prev => ({
      ...prev,
      tickSize,
      tickValue: tickValueInput,
      contractMultiplier: tickSize > 0 ? tickValueInput / tickSize : 0,
    }));
  } else {
    setForm(prev => ({
      ...prev,
      tickSize,
      contractMultiplier: contractMultiplierInput,
      tickValue: contractMultiplierInput * tickSize,
    }));
  }
  // eslint-disable-next-line
}, [tickSizeStr, editMode]);

    // Parse tickSizeStr en nombre, retourne 0 si invalide
  const parseTickSize = (str: string): number => {
    if (!str) return 0;
    const val = parseFloat(str);
    return isNaN(val) ? 0 : val;
  };
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };
const handleModeChange = (mode: 'tickValue' | 'contractMultiplier') => {
  setEditMode(mode);
  setForm(prev => {
    const tickSize = parseTickSize(tickSizeStr);
    if (mode === 'tickValue') {
      return {
        ...prev,
        tickValue: tickValueInput,
        contractMultiplier: tickSize > 0 ? tickValueInput / tickSize : 0,
      };
    } else {
      return {
        ...prev,
        contractMultiplier: contractMultiplierInput,
        tickValue: contractMultiplierInput * tickSize,
      };
    }
  });
};

const handleTickSizeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
  const val = e.target.value;
  if (/^[0-9.]*$/.test(val)) {
    setTickSizeStr(val);
  }
};

const handleTickValueChange = (e: React.ChangeEvent<HTMLInputElement>) => {
  const val = parseFloat(e.target.value);
  if (!isNaN(val)) {
    setTickValueInput(val);
    setForm(prev => {
      const tickSize = parseTickSize(tickSizeStr);
      return {
        ...prev,
        tickValue: val,
        contractMultiplier: tickSize > 0 ? val / tickSize : 0,
      };
    });
  } else {
    setTickValueInput(0);
    setForm(prev => ({ ...prev, tickValue: 0, contractMultiplier: 0 }));
  }
};

const handleContractMultiplierChange = (e: React.ChangeEvent<HTMLInputElement>) => {
  const val = parseFloat(e.target.value);
  if (!isNaN(val)) {
    setContractMultiplierInput(val);
    setForm(prev => {
      const tickSize = parseTickSize(tickSizeStr);
      return {
        ...prev,
        contractMultiplier: val,
        tickValue: val * tickSize,
      };
    });
  } else {
    setContractMultiplierInput(0);
    setForm(prev => ({ ...prev, contractMultiplier: 0, tickValue: 0 }));
  }
};

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (form.tickSize <= 0) {
      alert('Veuillez saisir un Tick Size strictement supérieur à 0.');
      return;
    }

    try {
      await axios.post('/api/futures', form);
      alert('Future créé avec succès');
      // reset ou autre action après succès si nécessaire
    } catch (err) {
      alert('Erreur lors de la création');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 600, margin: 'auto', display: 'flex', flexDirection: 'column', gap: '1rem' }}>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Symbol</label>
        <input name="symbol" value={form.symbol} onChange={handleChange} required className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Description</label>
        <input name="description" value={form.description} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">ISIN</label>
        <input name="isin" value={form.isin} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Expiration Code</label>
        <input name="expirationCode" value={form.expirationCode} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Parent Ticker</label>
        <input name="parentTicker" value={form.parentTicker} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Full Name</label>
        <input name="fullName" value={form.fullName} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Segment</label>
        <input name="segment" value={form.segment} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Maturity Date</label>
        <input type="date" name="maturityDate" value={form.maturityDate} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">First Trading Date</label>
        <input type="date" name="firstTradingDate" value={form.firstTradingDate} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Last Trading Date</label>
        <input type="date" name="lastTraadingDate" value={form.lastTraadingDate} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Initial Margin Amount</label>
        <input type="number" name="initialMarginAmount" value={form.initialMarginAmount} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Percentage Margin</label>
        <input type="number" name="percentageMargin" value={form.percentageMargin} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Lot Size</label>
        <input type="number" name="lotSize" value={form.lotSize} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Trading Currency</label>
        <input name="tradingCurrency" value={form.tradingCurrency} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>

      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Underlying Type</label>
        <select name="underlyingType" value={form.underlyingType} onChange={handleChange} required className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500">
          <option value="">Sélectionnez</option>
          {underlyingTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>
      </div>

      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Underlying Asset</label>
        <select name="underlyingId" value={form.underlyingId} onChange={handleChange} required disabled={!form.underlyingType} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500">
          <option value={0}>Sélectionnez</option>
          {underlyingAssets.map(asset => (
            <option key={asset.id} value={asset.id}>{asset.identifier}</option>
          ))}
        </select>
      </div>

      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Settlement Method</label>
        <select name="settlementMethod" value={form.settlementMethod} onChange={handleChange} required className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500">
          <option value="">Sélectionnez</option>
          {settlementMethods.map(method => (
            <option key={method} value={method}>{method}</option>
          ))}
        </select>
      </div>

      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Instrument Actif</label>
        <input type="checkbox" name="instrumentStatus" checked={form.instrumentStatus} onChange={handleChange} className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500" />
      </div>

      <div className="mb-4">
        <label className="block mb-1 font-medium text-gray-700">Deposit Type</label>
        <select name="depositType" value={form.depositType} onChange={handleChange} required className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500">
          <option value="">Sélectionnez</option>
          {depositTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>
      </div>

       {/* Replace the mode selection and tick value/contract multiplier section with a segmented button and styled fields */}
<div className="flex space-x-2 mb-2">
  <button
    className={`px-4 py-2 rounded ${editMode === 'tickValue' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
    onClick={() => handleModeChange('tickValue')}
    type="button"
  >
    Saisir Tick Value
  </button>
  <button
    className={`px-4 py-2 rounded ${editMode === 'contractMultiplier' ? 'bg-blue-600 text-white' : 'bg-gray-200'}`}
    onClick={() => handleModeChange('contractMultiplier')}
    type="button"
  >
    Saisir Contract Multiplier
  </button>
</div>

<div className="mb-2">
  <label className="block mb-1">Tick Size</label>
  <input
    type="text"
    name="tickSize"
    value={tickSizeStr}
    onChange={handleTickSizeChange}
    placeholder="Exemple: 0.01"
    className="border rounded px-2 py-1 w-full"
  />
</div>

<div className="mb-2">
  <label className="block mb-1">Tick Value {editMode === 'tickValue' ? '' : '(calculé)'}</label>
  <input
    type="number"
    step="0.0001"
    min={0}
    name="tickValue"
    value={editMode === 'tickValue' ? tickValueInput : form.tickValue}
    onChange={handleTickValueChange}
    readOnly={editMode !== 'tickValue'}
    className={`border rounded px-2 py-1 w-full ${editMode !== 'tickValue' ? 'bg-gray-100 text-gray-500' : ''}`}
  />
</div>
<div className="mb-2">
  <label className="block mb-1">Contract Multiplier {editMode === 'contractMultiplier' ? '' : '(calculé)'}</label>
  <input
    type="number"
    step="0.0001"
    min={0}
    name="contractMultiplier"
    value={editMode === 'contractMultiplier' ? contractMultiplierInput : form.contractMultiplier}
    onChange={handleContractMultiplierChange}
    readOnly={editMode !== 'contractMultiplier'}
    className={`border rounded px-2 py-1 w-full ${editMode !== 'contractMultiplier' ? 'bg-gray-100 text-gray-500' : ''}`}
  />
</div>

      <button type="submit" className="mt-6 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition">Créer Future</button>
    </form>
  );
};

export default FutureCreationForm;
