import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '',
    description: '',
    isin: '',
    expirationCode: '',
    parentTicker: '',
    fullName: '',
    segment: '',
    maturityDate: '',
    firstTradingDate: '',
    lastTraadingDate: '',
    initialMarginAmount: 0,
    percentageMargin: 0,
    lotSize: 0,
    contractMultiplier: 0,
    tradingCurrency: '',
    underlyingType: '',
    underlyingId: 0,
    settlementMethod: '',
    instrumentStatus: false,
    tickSize: 0,
    tickValue: 0,
    depositType: '',
  });

  const [settlementMethods, setSettlementMethods] = useState<string[]>([]);
  const [depositTypes, setDepositTypes] = useState<string[]>([]);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>([]);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([]);

  useEffect(() => {
    async function fetchEnums() {
      const [settlementRes, depositRes, typeRes] = await Promise.all([
        axios.get<string[]>('/api/enums/settlement-methods'),
        axios.get<string[]>('/api/enums/deposit-types'),
        axios.get<string[]>('/api/underlyings/types'),
      ]);
      setSettlementMethods(settlementRes.data);
      setDepositTypes(depositRes.data);
      setUnderlyingTypes(typeRes.data);
    }
    fetchEnums();
  }, []);

  useEffect(() => {
    if (form.underlyingType) {
      axios.get<{ id: number; identifier: string }[]>(`/api/underlying-assets?type=${form.underlyingType}`)
        .then(res => setUnderlyingAssets(res.data))
        .catch(() => setUnderlyingAssets([]));
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    } else {
      setUnderlyingAssets([]);
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    }
  }, [form.underlyingType]);

  const isRate = form.depositType === 'RATE';
  const isAmount = form.depositType === 'AMOUNT';

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handlePercentageMarginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    setForm(prev => ({
      ...prev,
      percentageMargin: val,
      initialMarginAmount: prev.lotSize * val,
    }));
  };

  const handleInitialMarginAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    setForm(prev => ({
      ...prev,
      initialMarginAmount: val,
      percentageMargin: prev.lotSize > 0 ? val / prev.lotSize : 0,
    }));
  };

  useEffect(() => {
    setForm(prev => {
      if (isRate) {
        return { ...prev, initialMarginAmount: prev.lotSize * prev.percentageMargin };
      } else if (isAmount) {
        return { ...prev, percentageMargin: prev.lotSize > 0 ? prev.initialMarginAmount / prev.lotSize : 0 };
      }
      return prev;
    });
  }, [form.lotSize, form.depositType]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await axios.post('/api/futures', form);
      alert('Future créé avec succès');
    } catch (err) {
      alert('Erreur lors de la création');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-3xl mx-auto p-4 space-y-6">
      <section className="p-4 border rounded space-y-4 bg-gray-50">
        <h2 className="text-lg font-bold">Informations Générales</h2>
        <div className="grid grid-cols-2 gap-4">
          <input name="symbol" placeholder="Symbol" value={form.symbol} onChange={handleChange} className="border p-2 rounded" required />
          <input name="description" placeholder="Description" value={form.description} onChange={handleChange} className="border p-2 rounded" />
          <input name="isin" placeholder="ISIN" value={form.isin} onChange={handleChange} className="border p-2 rounded" />
          <input name="expirationCode" placeholder="Expiration Code" value={form.expirationCode} onChange={handleChange} className="border p-2 rounded" />
          <input name="parentTicker" placeholder="Parent Ticker" value={form.parentTicker} onChange={handleChange} className="border p-2 rounded" />
          <input name="fullName" placeholder="Full Name" value={form.fullName} onChange={handleChange} className="border p-2 rounded" />
          <input name="segment" placeholder="Segment" value={form.segment} onChange={handleChange} className="border p-2 rounded" />
          <input type="date" name="maturityDate" placeholder="Maturity Date" value={form.maturityDate} onChange={handleChange} className="border p-2 rounded" />
          <input type="date" name="firstTradingDate" placeholder="First Trading Date" value={form.firstTradingDate} onChange={handleChange} className="border p-2 rounded" />
          <input type="date" name="lastTraadingDate" placeholder="Last Trading Date" value={form.lastTraadingDate} onChange={handleChange} className="border p-2 rounded" />
          <input name="tradingCurrency" placeholder="Trading Currency" value={form.tradingCurrency} onChange={handleChange} className="border p-2 rounded" />
        </div>
      </section>

      <section className="p-4 border rounded space-y-4 bg-gray-50">
        <h2 className="text-lg font-bold">Paramètres Financiers</h2>
        <select name="depositType" value={form.depositType} onChange={handleChange} className="border p-2 rounded w-full">
          <option value="">Sélectionnez le Deposit Type</option>
          {depositTypes.map(type => (<option key={type} value={type}>{type}</option>))}
        </select>
        <input type="number" name="lotSize" placeholder="Lot Size" value={form.lotSize} onChange={handleChange} className="border p-2 rounded w-full" />
        <input type="number" name="initialMarginAmount" placeholder="Initial Margin Amount" value={form.initialMarginAmount} onChange={handleInitialMarginAmountChange} readOnly={!isAmount} className="border p-2 rounded w-full" />
        <input type="number" name="percentageMargin" placeholder="Percentage Margin" value={form.percentageMargin} onChange={handlePercentageMarginChange} readOnly={!isRate} className="border p-2 rounded w-full" />
      </section>

      <section className="p-4 border rounded space-y-4 bg-gray-50">
        <h2 className="text-lg font-bold">Paramètres Underlying</h2>
        <select name="underlyingType" value={form.underlyingType} onChange={handleChange} className="border p-2 rounded w-full">
          <option value="">Sélectionnez le Underlying Type</option>
          {underlyingTypes.map(type => (<option key={type} value={type}>{type}</option>))}
        </select>
        <select name="underlyingId" value={form.underlyingId} onChange={handleChange} disabled={!form.underlyingType} className="border p-2 rounded w-full">
          <option value={0}>Sélectionnez l'Asset</option>
          {underlyingAssets.map(asset => (<option key={asset.id} value={asset.id}>{asset.identifier}</option>))}
        </select>
      </section>

      <section className="p-4 border rounded space-y-4 bg-gray-50">
        <h2 className="text-lg font-bold">Autres Informations</h2>
        <select name="settlementMethod" value={form.settlementMethod} onChange={handleChange} className="border p-2 rounded w-full">
          <option value="">Sélectionnez le Settlement Method</option>
          {settlementMethods.map(method => (<option key={method} value={method}>{method}</option>))}
        </select>
        <label className="flex items-center space-x-2">
          <input type="checkbox" name="instrumentStatus" checked={form.instrumentStatus} onChange={handleChange} className="border rounded" />
          <span>Instrument Actif</span>
        </label>
      </section>

      <button type="submit" className="bg-blue-600 text-white p-3 rounded w-full">Créer Future</button>
    </form>
  );
};

export default FutureCreationForm;
