import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '',
    description: '',
    isin: '',
    expirationCode: '',
    parentTicker: '',
    fullName: '',
    segment: '',
    maturityDate: '',
    firstTradingDate: '',
    lastTraadingDate: '',
    initialMarginAmount: 0,
    percentageMargin: 0,
    lotSize: 0,
    contractMultiplier: 0,
    tradingCurrency: '',
    underlyingType: '',
    underlyingId: 0,
    settlementMethod: '',
    instrumentStatus: false,
    tickSize: 0,
    tickValue: 0,
    depositType: '',
  });

  const [editMode, setEditMode] = useState<'tickValue' | 'contractMultiplier'>('tickValue');
  const [tickSizeStr, setTickSizeStr] = useState('');
  const [tickValueInput, setTickValueInput] = useState(0);
  const [contractMultiplierInput, setContractMultiplierInput] = useState(0);

  const [settlementMethods, setSettlementMethods] = useState<string[]>([]);
  const [depositTypes, setDepositTypes] = useState<string[]>([]);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>([]);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([]);

  useEffect(() => {
    async function fetchEnums() {
      const [settlementRes, depositRes, typeRes] = await Promise.all([
        axios.get<string[]>('/api/enums/settlement-methods'),
        axios.get<string[]>('/api/enums/deposit-types'),
        axios.get<string[]>('/api/underlyings/types'),
      ]);
      setSettlementMethods(settlementRes.data);
      setDepositTypes(depositRes.data);
      setUnderlyingTypes(typeRes.data);
    }
    fetchEnums();
  }, []);

  useEffect(() => {
    if (form.underlyingType) {
      axios.get<{ id: number; identifier: string }[]>(`/api/underlying-assets?type=${form.underlyingType}`)
        .then(res => setUnderlyingAssets(res.data))
        .catch(() => setUnderlyingAssets([]));
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    } else {
      setUnderlyingAssets([]);
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    }
  }, [form.underlyingType]);

  useEffect(() => {
    setTickSizeStr(form.tickSize > 0 ? form.tickSize.toString() : '');
  }, [form.tickSize]);

  const parseTickSize = (str: string): number => {
    if (!str) return 0;
    const val = parseFloat(str);
    return isNaN(val) ? 0 : val;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleModeChange = (mode: 'tickValue' | 'contractMultiplier') => {
    setEditMode(mode);
    setForm(prev => {
      const tickSize = parseTickSize(tickSizeStr);
      if (mode === 'tickValue') {
        return {
          ...prev,
          tickValue: tickValueInput,
          contractMultiplier: tickSize > 0 ? tickValueInput / tickSize : 0,
        };
      } else {
        return {
          ...prev,
          contractMultiplier: contractMultiplierInput,
          tickValue: contractMultiplierInput * tickSize,
        };
      }
    });
  };

  const handleTickSizeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = e.target.value;
    if (/^[0-9.]*$/.test(val)) {
      setTickSizeStr(val);
    }
  };

  const handleTickValueChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    if (!isNaN(val)) {
      setTickValueInput(val);
      setForm(prev => {
        const tickSize = parseTickSize(tickSizeStr);
        return {
          ...prev,
          tickValue: val,
          contractMultiplier: tickSize > 0 ? val / tickSize : 0,
        };
      });
    }
  };

  const handleContractMultiplierChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    if (!isNaN(val)) {
      setContractMultiplierInput(val);
      setForm(prev => {
        const tickSize = parseTickSize(tickSizeStr);
        return {
          ...prev,
          contractMultiplier: val,
          tickValue: val * tickSize,
        };
      });
    }
  };

  const isRate = form.depositType === 'RATE';
  const isAmount = form.depositType === 'AMOUNT';

  const handlePercentageMarginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    setForm(prev => ({
      ...prev,
      percentageMargin: val,
      initialMarginAmount: prev.lotSize * val,
    }));
  };

  const handleInitialMarginAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    setForm(prev => ({
      ...prev,
      initialMarginAmount: val,
      percentageMargin: prev.lotSize > 0 ? val / prev.lotSize : 0,
    }));
  };

  useEffect(() => {
    setForm(prev => {
      if (isRate) {
        return { ...prev, initialMarginAmount: prev.lotSize * prev.percentageMargin };
      } else if (isAmount) {
        return { ...prev, percentageMargin: prev.lotSize > 0 ? prev.initialMarginAmount / prev.lotSize : 0 };
      }
      return prev;
    });
  }, [form.lotSize, form.depositType]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (form.tickSize <= 0) {
      alert('Veuillez saisir un Tick Size strictement supérieur à 0.');
      return;
    }
    try {
      await axios.post('/api/futures', form);
      alert('Future créé avec succès');
    } catch (err) {
      alert('Erreur lors de la création');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 600, margin: 'auto', display: 'flex', flexDirection: 'column', gap: '1rem' }}>
      {/* Autres champs ici */}

      <div className="mb-6 p-4 border rounded bg-gray-50 space-y-4">
        <div>
          <label className="block mb-1 font-medium text-gray-700">Deposit Type</label>
          <select
            name="depositType"
            value={form.depositType}
            onChange={handleChange}
            required
            className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="">Sélectionnez</option>
            {depositTypes.map(type => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Lot Size</label>
          <input
            type="number"
            name="lotSize"
            min="0"
            step="1"
            value={form.lotSize}
            onChange={(e) => {
              const val = parseFloat(e.target.value);
              setForm(prev => ({
                ...prev,
                lotSize: val,
                initialMarginAmount: isRate ? val * prev.percentageMargin : prev.initialMarginAmount,
                percentageMargin: isAmount && val > 0 ? prev.initialMarginAmount / val : prev.percentageMargin
              }));
            }}
            className="border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Initial Margin Amount {isAmount ? '' : '(calculé)'}</label>
          <input
            type="number"
            name="initialMarginAmount"
            min="0"
            step="0.01"
            value={form.initialMarginAmount}
            onChange={handleInitialMarginAmountChange}
            readOnly={!isAmount}
            className={`border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500 ${!isAmount ? 'bg-gray-100 text-gray-500' : ''}`}
          />
        </div>

        <div>
          <label className="block mb-1 font-medium text-gray-700">Percentage Margin {isRate ? '' : '(calculé)'}</label>
          <input
            type="number"
            name="percentageMargin"
            min="0"
            step="0.0001"
            value={form.percentageMargin}
            onChange={handlePercentageMarginChange}
            readOnly={!isRate}
            className={`border rounded px-2 py-1 w-full focus:outline-none focus:ring-2 focus:ring-blue-500 ${!isRate ? 'bg-gray-100 text-gray-500' : ''}`}
          />
        </div>
      </div>

      <button type="submit" className="mt-6 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition">Créer Future</button>
    </form>
  );
};

export default FutureCreationForm;
