import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  ChevronRight, ChevronDown, Calendar, Info, DollarSign,
  TrendingUp, Settings, CheckCircle2, AlertCircle
} from 'lucide-react';

interface FutureCreationDTO {
  symbol: string;
  description: string;
  isin: string;
  expirationCode: string;
  parentTicker: string;
  fullName: string;
  segment: string;
  maturityDate: string;
  firstTradingDate: string;
  lastTraadingDate: string;
  initialMarginAmount: number;
  percentageMargin: number;
  lotSize: number;
  contractMultiplier: number;
  tradingCurrency: string;
  underlyingType: string;
  underlyingId: number;
  settlementMethod: string;
  instrumentStatus: boolean;
  tickSize: number;
  tickValue: number;
  depositType: string;
}

const FutureCreationForm: React.FC = () => {
  const [form, setForm] = useState<FutureCreationDTO>({
    symbol: '', description: '', isin: '', expirationCode: '',
    parentTicker: '', fullName: '', segment: '', maturityDate: '',
    firstTradingDate: '', lastTraadingDate: '', initialMarginAmount: 0,
    percentageMargin: 0, lotSize: 0, contractMultiplier: 0,
    tradingCurrency: '', underlyingType: '', underlyingId: 0,
    settlementMethod: '', instrumentStatus: false, tickSize: 0,
    tickValue: 0, depositType: '',
  });
  const [editMode, setEditMode] = useState<'tickValue' | 'contractMultiplier'>('tickValue');
  const [tickSizeStr, setTickSizeStr] = useState('');
  const [percentageMarginStr, setPercentageMarginStr] = useState('');
  const [tickValueInput, setTickValueInput] = useState(0);
  const [contractMultiplierInput, setContractMultiplierInput] = useState(0);
  const [initialMarginAmountStr, setInitialMarginAmountStr] = useState('');

  const [settlementMethods, setSettlementMethods] = useState<string[]>([]);
  const [depositTypes, setDepositTypes] = useState<string[]>([]);
  const [underlyingTypes, setUnderlyingTypes] = useState<string[]>([]);
  const [underlyingAssets, setUnderlyingAssets] = useState<{ id: number; identifier: string }[]>([]);

  const isRate = form.depositType === 'RATE';
  const isAmount = form.depositType === 'AMOUNT';

  useEffect(() => {
    async function fetchEnums() {
      const [settlementRes, depositRes, typeRes] = await Promise.all([
        axios.get<string[]>('/api/enums/settlement-methods'),
        axios.get<string[]>('/api/enums/deposit-types'),
        axios.get<string[]>('/api/underlyings/types')
      ]);
      setSettlementMethods(settlementRes.data);
      setDepositTypes(depositRes.data);
      setUnderlyingTypes(typeRes.data);
    }
    fetchEnums();
  }, []);

  useEffect(() => {
    if (form.underlyingType) {
      axios.get<{ id: number; identifier: string }[]>(`/api/underlying-assets?type=${form.underlyingType}`)
        .then(res => setUnderlyingAssets(res.data))
        .catch(() => setUnderlyingAssets([]));
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    } else {
      setUnderlyingAssets([]);
      setForm(prev => ({ ...prev, underlyingId: 0 }));
    }
  }, [form.underlyingType]);

  useEffect(() => {
    setTickSizeStr(form.tickSize > 0 ? form.tickSize.toString() : '');
  }, [form.tickSize]);

  useEffect(() => {
    setPercentageMarginStr(form.percentageMargin > 0 ? form.percentageMargin.toString() : '');
  }, [form.percentageMargin]);

  useEffect(() => {
    setInitialMarginAmountStr(form.initialMarginAmount > 0 ? form.initialMarginAmount.toString() : '');
  }, [form.initialMarginAmount]);

  useEffect(() => {
    const tickSize = parseFloat(tickSizeStr) || 0;
    if (editMode === 'tickValue') {
      const contractMultiplierCalc = tickSize > 0 ? tickValueInput / tickSize : 0;
      setForm(prev => ({ ...prev, tickSize, tickValue: tickValueInput, contractMultiplier: contractMultiplierCalc }));
      setContractMultiplierInput(contractMultiplierCalc);
    } else {
      const tickValueCalc = contractMultiplierInput * tickSize;
      setForm(prev => ({ ...prev, tickSize, contractMultiplier: contractMultiplierInput, tickValue: tickValueCalc }));
      setTickValueInput(tickValueCalc);
    }
  }, [tickSizeStr, editMode, tickValueInput, contractMultiplierInput]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleInitialMarginAmountInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const str = e.target.value;
    setInitialMarginAmountStr(str);
    const val = parseFloat(str) || 0;
    setForm(prev => ({ ...prev, initialMarginAmount: val, percentageMargin: prev.lotSize > 0 ? val / prev.lotSize : 0 }));
  };

  const handlePercentageMarginInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const str = e.target.value;
    setPercentageMarginStr(str);
    const val = parseFloat(str) || 0;
    setForm(prev => ({ ...prev, percentageMargin: val, initialMarginAmount: prev.lotSize * val }));
  };

  const handleTickSizeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTickSizeStr(e.target.value);
  };

  const handleTickValueChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    setTickValueInput(val);
  };

  const handleContractMultiplierChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = parseFloat(e.target.value);
    setContractMultiplierInput(val);
  };

  const handleModeChange = (mode: 'tickValue' | 'contractMultiplier') => {
    setEditMode(mode);
  };

  useEffect(() => {
    setForm(prev => {
      if (isRate) {
        return { ...prev, initialMarginAmount: prev.lotSize * prev.percentageMargin };
      } else if (isAmount) {
        return { ...prev, percentageMargin: prev.lotSize > 0 ? prev.initialMarginAmount / prev.lotSize : 0 };
      }
      return prev;
    });
  }, [form.lotSize, form.depositType]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await axios.post('/api/futures', form);
      alert('Future créé avec succès');
    } catch (err) {
      alert('Erreur lors de la création');
      console.error(err);
    }
  };

  return (
   <form onSubmit={handleSubmit} className="max-w-4xl mx-auto p-6 space-y-8">
      <section className="p-4 border rounded space-y-6 bg-gray-100">
        <h2 className="text-2xl font-bold mb-4">Identification de l'instrument</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label htmlFor="symbol" className="block mb-1 font-semibold">Symbole</label>
            <input type="text" id="symbol" name="symbol" value={formData.symbol} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="description" className="block mb-1 font-semibold">Description</label>
            <input type="text" id="description" name="description" value={formData.description} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="isin" className="block mb-1 font-semibold">ISIN</label>
            <input type="text" id="isin" name="isin" value={formData.isin} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="expirationCode" className="block mb-1 font-semibold">Code d'expiration</label>
            <input type="text" id="expirationCode" name="expirationCode" value={formData.expirationCode} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="parentTicker" className="block mb-1 font-semibold">Ticker parent</label>
            <input type="text" id="parentTicker" name="parentTicker" value={formData.parentTicker} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="fullName" className="block mb-1 font-semibold">Nom complet</label>
            <input type="text" id="fullName" name="fullName" value={formData.fullName} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="segment" className="block mb-1 font-semibold">Segment</label>
            <input type="text" id="segment" name="segment" value={formData.segment} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="maturityDate" className="block mb-1 font-semibold">Date d'échéance</label>
            <input type="date" id="maturityDate" name="maturityDate" value={formData.maturityDate} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="firstTradingDate" className="block mb-1 font-semibold">Première date de cotation</label>
            <input type="date" id="firstTradingDate" name="firstTradingDate" value={formData.firstTradingDate} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="lastTradingDate" className="block mb-1 font-semibold">Dernière date de cotation</label>
            <input type="date" id="lastTradingDate" name="lastTradingDate" value={formData.lastTradingDate} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
        </div>
      </section>

      <section className="p-4 border rounded space-y-6 bg-gray-100">
        <h2 className="text-2xl font-bold mb-4">Spécifications financières</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label htmlFor="tickSize" className="block mb-1 font-semibold">Taille du tick</label>
            <input type="number" step="any" id="tickSize" name="tickSize" value={formData.tickSize} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="tickValue" className="block mb-1 font-semibold">Valeur du tick</label>
            <input type="number" step="any" id="tickValue" name="tickValue" value={formData.tickValue ?? ''} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="contractMultiplier" className="block mb-1 font-semibold">Multiplicateur</label>
            <input type="number" step="any" id="contractMultiplier" name="contractMultiplier" value={formData.contractMultiplier ?? ''} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="currency" className="block mb-1 font-semibold">Devise</label>
            <input type="text" id="currency" name="currency" value={formData.currency} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="marginCurrency" className="block mb-1 font-semibold">Devise de marge</label>
            <input type="text" id="marginCurrency" name="marginCurrency" value={formData.marginCurrency} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="initialMarginAmount" className="block mb-1 font-semibold">Marge initiale</label>
            <input type="number" step="any" id="initialMarginAmount" name="initialMarginAmount" value={formData.initialMarginAmount} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="maintenanceMarginAmount" className="block mb-1 font-semibold">Marge de maintenance</label>
            <input type="number" step="any" id="maintenanceMarginAmount" name="maintenanceMarginAmount" value={formData.maintenanceMarginAmount} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="percentageMargin" className="block mb-1 font-semibold">Marge en %</label>
            <input type="number" step="any" id="percentageMargin" name="percentageMargin" value={formData.percentageMargin} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
          <div>
            <label htmlFor="status" className="block mb-1 font-semibold">Statut</label>
            <input type="text" id="status" name="status" value={formData.status} onChange={handleChange} className="w-full border p-2 rounded" />
          </div>
        </div>
      </section>

      <div className="text-center">
        <button type="submit" className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Créer le futur</button>
      </div>
    </form>
  );
};

export default FutureCreationForm;
