import React, { useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Info, DollarSign, Calendar } from 'lucide-react';
import { useFutureForm } from './hooks/useFutureForm';
import { useFutureAPI } from './hooks/useFutureAPI';
import { useFormNavigation } from './hooks/useFormNavigation';
import { FormStepper } from './common/FormStepper';
import { FormField } from './common/FormField';
import { IdentificationSection } from './sections/IdentificationSection';
import { fieldLabels } from './schemas/futureFormSchema';

// Define form sections
const formSections = [
  {
    id: 'identification',
    title: "Identification de l'instrument",
    icon: <Info className="w-5 h-5" />,
    description: "Informations de base",
    fields: ['symbol', 'description', 'isin', 'fullName']
  },
  {
    id: 'deposit',
    title: "Dépôt & sous-jacents",
    icon: <DollarSign className="w-5 h-5" />,
    description: "Configuration des marges",
    fields: ['depositType', 'lotSize', 'initialMarginAmount', 'percentageMargin', 'underlyingType', 'underlyingId']
  },
  {
    id: 'trading',
    title: "Négociation",
    icon: <Calendar className="w-5 h-5" />,
    description: "Paramètres de trading",
    fields: ['firstTradingDate', 'lastTraadingDate', 'tradingCurrency', 'tickSize', 'settlementMethod']
  }
];

const FutureCreationForm: React.FC = () => {
  // Use custom hooks
  const formHook = useFutureForm();
  const apiHook = useFutureAPI();
  const navigationHook = useFormNavigation(formSections, formHook.validateFields);

  // Destructure what we need
  const {
    form,
    errors,
    isSubmitting,
    tickSizeStr,
    percentageMarginStr,
    initialMarginAmountStr,
    tickValueInput,
    contractMultiplierInput,
    editMode,
    isRate,
    isAmount,
    handleFieldChange,
    handleTickSizeChange,
    handleTickValueChange,
    handleContractMultiplierChange,
    handlePercentageMarginInputChange,
    handleInitialMarginAmountInputChange,
    handleModeChange,
    validateForm,
    clearFieldErrors,
    resetForm,
    setIsSubmitting
  } = formHook;

  const {
    settlementMethods,
    depositTypes,
    underlyingTypes,
    underlyingAssets,
    isLoading: apiLoading,
    error: apiError,
    fetchUnderlyingAssets,
    createFuture,
    clearError: clearApiError
  } = apiHook;

  const {
    currentStep,
    completedSteps,
    nextStep,
    prevStep,
    jumpToStep,
    getProgressPercentage,
    getCurrentSection,
    isLastStep,
    isFirstStep
  } = navigationHook;

  // Fetch underlying assets when type changes
  useEffect(() => {
    if (form.underlyingType) {
      fetchUnderlyingAssets(form.underlyingType);
      formHook.updateField('underlyingId', 0);
    } else {
      formHook.updateField('underlyingId', 0);
    }
  }, [form.underlyingType, fetchUnderlyingAssets]);

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!isLastStep()) return;
    if (isSubmitting) return;
    
    if (!validateForm()) {
      alert('Veuillez corriger les erreurs avant de soumettre le formulaire');
      return;
    }

    try {
      setIsSubmitting(true);
      await createFuture(form);
      alert('Future créé avec succès');
      resetForm();
      // Optionally redirect or show success message
    } catch (err) {
      // Error is already handled by the API hook
      console.error('Form submission error:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Handle step navigation
  const handleNextStep = () => {
    if (nextStep()) {
      // Clear errors for the next step
      const nextSection = formSections[currentStep + 1];
      if (nextSection) {
        clearFieldErrors(nextSection.fields);
      }
    }
  };

  const handlePrevStep = () => {
    prevStep();
  };

  const handleStepClick = (stepIndex: number) => {
    if (jumpToStep(stepIndex)) {
      // Clear errors for the new step
      const section = formSections[stepIndex];
      if (section) {
        clearFieldErrors(section.fields);
      }
    }
  };

  // Animation variants
  const pageTransition = {
    initial: { opacity: 0, x: -20 },
    animate: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: 20 }
  };

  // Render step content
  const renderStepContent = () => {
    const currentSection = getCurrentSection();
    
    return (
      <AnimatePresence mode="wait">
        <motion.div
          key={currentStep}
          initial="initial"
          animate="animate"
          exit="exit"
          variants={pageTransition}
          transition={{ duration: 0.3 }}
          className="min-h-[400px]"
        >
          {/* Step 1: Identification */}
          {currentStep === 0 && (
            <IdentificationSection
              form={form}
              errors={errors}
              onChange={handleFieldChange}
            />
          )}

          {/* Step 2: Deposit & Underlying */}
          {currentStep === 1 && (
            <div className="space-y-3">
              {/* Deposit Type Toggle */}
              <div className="flex justify-center space-x-2 my-2">
                <button
                  type="button"
                  onClick={() => formHook.updateField('depositType', 'AMOUNT')}
                  className={`px-3 py-2 rounded-lg font-medium transition-all duration-300 transform
                    ${isAmount 
                      ? 'bg-teal-600 text-white scale-105 shadow-lg' 
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}`}
                >
                  Montant fixe
                </button>
                <button
                  type="button"
                  onClick={() => formHook.updateField('depositType', 'RATE')}
                  className={`px-3 py-2 rounded-lg font-medium transition-all duration-300 transform
                    ${isRate 
                      ? 'bg-teal-600 text-white scale-105 shadow-lg' 
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}`}
                >
                  Pourcentage
                </button>
              </div>

              {/* Form Fields */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <FormField
                  label={fieldLabels.lotSize}
                  name="lotSize"
                  value={form.lotSize}
                  onChange={handleFieldChange}
                  error={errors.lotSize}
                  type="number"
                  required
                  min={1}
                />
                
                <FormField
                  label={fieldLabels.depositType}
                  name="depositType"
                  value={form.depositType}
                  onChange={handleFieldChange}
                  error={errors.depositType}
                  type="select"
                  required
                  options={depositTypes.map(type => ({ value: type, label: type }))}
                />
                
                <FormField
                  label={fieldLabels.initialMarginAmount}
                  name="initialMarginAmount"
                  value={initialMarginAmountStr}
                  onChange={handleInitialMarginAmountInputChange}
                  error={errors.initialMarginAmount}
                  readOnly={!isAmount}
                  tooltip={!isAmount ? "Calculé automatiquement" : undefined}
                />
                
                <FormField
                  label={fieldLabels.percentageMargin}
                  name="percentageMargin"
                  value={percentageMarginStr}
                  onChange={handlePercentageMarginInputChange}
                  error={errors.percentageMargin}
                  readOnly={!isRate}
                  tooltip={!isRate ? "Calculé automatiquement" : undefined}
                />
              </div>

              {/* Underlying Assets */}
              <div className="bg-white rounded-lg shadow p-3 border border-gray-100">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  <FormField
                    label={fieldLabels.underlyingType}
                    name="underlyingType"
                    value={form.underlyingType}
                    onChange={handleFieldChange}
                    error={errors.underlyingType}
                    type="select"
                    required
                    options={underlyingTypes.map(type => ({ value: type, label: type }))}
                  />
                  
                  <FormField
                    label={fieldLabels.underlyingId}
                    name="underlyingId"
                    value={form.underlyingId}
                    onChange={handleFieldChange}
                    error={errors.underlyingId}
                    type="select"
                    required
                    disabled={!form.underlyingType}
                    options={underlyingAssets.map(asset => ({ value: asset.id.toString(), label: asset.identifier }))}
                  />
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Trading */}
          {currentStep === 2 && (
            <div className="space-y-3">
              {/* Tick Configuration */}
              <div className="bg-gradient-to-r from-teal-50 to-white p-3 rounded-lg space-y-3">
                <div className="flex items-end space-x-4 mb-2">
                  <div className="flex-1">
                    <FormField
                      label={fieldLabels.tickSize}
                      name="tickSize"
                      value={tickSizeStr}
                      onChange={handleTickSizeChange}
                      error={errors.tickSize}
                      placeholder="Ex: 0.01"
                      required
                    />
                  </div>
                  
                  {/* Mode Toggle */}
                  <div className="flex space-x-2">
                    <button
                      type="button"
                      onClick={() => handleModeChange('tickValue')}
                      className={`px-3 py-2 rounded-lg font-medium transition-all duration-300 transform
                        ${editMode === 'tickValue' 
                          ? 'bg-teal-600 text-white scale-105 shadow-lg' 
                          : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}`}
                    >
                      Saisir Tick Value
                    </button>
                    <button
                      type="button"
                      onClick={() => handleModeChange('contractMultiplier')}
                      className={`px-3 py-2 rounded-lg font-medium transition-all duration-300 transform
                        ${editMode === 'contractMultiplier' 
                          ? 'bg-teal-600 text-white scale-105 shadow-lg' 
                          : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}`}
                    >
                      Saisir Multiplicateur
                    </button>
                  </div>
                </div>

                {/* Tick Value and Contract Multiplier */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <FormField
                    label={fieldLabels.tickValue}
                    name="tickValue"
                    value={editMode === 'tickValue' ? tickValueInput : form.tickValue}
                    onChange={handleTickValueChange}
                    error={errors.tickValue}
                    type="number"
                    readOnly={editMode !== 'tickValue'}
                    tooltip={editMode !== 'tickValue' ? "Calculé automatiquement" : undefined}
                  />
                  
                  <FormField
                    label={fieldLabels.contractMultiplier}
                    name="contractMultiplier"
                    value={editMode === 'contractMultiplier' ? contractMultiplierInput : form.contractMultiplier}
                    onChange={handleContractMultiplierChange}
                    error={errors.contractMultiplier}
                    type="number"
                    readOnly={editMode !== 'contractMultiplier'}
                    tooltip={editMode !== 'contractMultiplier' ? "Calculé automatiquement" : undefined}
                  />
                </div>
              </div>

              {/* Other Trading Fields */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <FormField
                  label={fieldLabels.firstTradingDate}
                  name="firstTradingDate"
                  value={form.firstTradingDate}
                  onChange={handleFieldChange}
                  error={errors.firstTradingDate}
                  type="date"
                  required
                />
                
                <FormField
                  label={fieldLabels.lastTraadingDate}
                  name="lastTraadingDate"
                  value={form.lastTraadingDate}
                  onChange={handleFieldChange}
                  error={errors.lastTraadingDate}
                  type="date"
                  required
                />
                
                <FormField
                  label={fieldLabels.tradingCurrency}
                  name="tradingCurrency"
                  value={form.tradingCurrency}
                  onChange={handleFieldChange}
                  error={errors.tradingCurrency}
                  required
                />
                
                <FormField
                  label={fieldLabels.settlementMethod}
                  name="settlementMethod"
                  value={form.settlementMethod}
                  onChange={handleFieldChange}
                  error={errors.settlementMethod}
                  type="select"
                  required
                  options={settlementMethods.map(method => ({ value: method, label: method }))}
                />
                
                {/* Instrument Status */}
                <div className="space-y-2">
                  <label className="block text-sm font-semibold text-gray-700">
                    {fieldLabels.instrumentStatus} <span className="text-red-500">*</span>
                  </label>
                  <div className="flex space-x-2">
                    <button
                      type="button"
                      onClick={() => formHook.updateField('instrumentStatus', true)}
                      className={`flex-1 py-3 px-4 rounded-lg font-semibold transition-all duration-200 ${
                        form.instrumentStatus 
                          ? 'bg-green-600 text-white shadow-lg' 
                          : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                      }`}
                    >
                      Côté
                    </button>
                    <button
                      type="button"
                      onClick={() => formHook.updateField('instrumentStatus', false)}
                      className={`flex-1 py-3 px-4 rounded-lg font-semibold transition-all duration-200 ${
                        !form.instrumentStatus 
                          ? 'bg-red-600 text-white shadow-lg' 
                          : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                      }`}
                    >
                      Non côté
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </motion.div>
      </AnimatePresence>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-teal-50 to-white py-2">
      <div className="max-w-4xl mx-auto px-2">
        {/* Header */}
        <div className="text-center mb-3">
          <h1 className="text-2xl font-bold text-gray-900">Créer un Future</h1>
          <p className="mt-1 text-xs text-gray-600">
            Remplissez les informations nécessaires pour créer un nouveau Future
          </p>
        </div>

        {/* API Error Display */}
        {apiError && (
          <div className="mb-3 p-3 bg-red-100 border border-red-300 rounded-lg">
            <p className="text-red-700 text-sm">{apiError}</p>
            <button
              onClick={clearApiError}
              className="mt-2 text-red-600 hover:text-red-800 text-xs underline"
            >
              Fermer
            </button>
          </div>
        )}

        {/* Stepper */}
        <FormStepper
          sections={formSections}
          currentStep={currentStep}
          completedSteps={completedSteps}
          onStepClick={handleStepClick}
        />

        {/* Form Content */}
        {isLastStep() ? (
          <form onSubmit={handleSubmit}>
            <div className="bg-white rounded-lg shadow p-2 relative overflow-hidden">
              {/* Section Header */}
              <div className="flex items-center mb-2">
                <div className="p-1.5 bg-teal-100 rounded mr-2">
                  {getCurrentSection().icon}
                </div>
                <div>
                  <h2 className="text-lg font-bold text-gray-800">
                    {getCurrentSection().title}
                  </h2>
                  <p className="text-xs text-gray-600">
                    {getCurrentSection().description}
                  </p>
                </div>
              </div>
              
              {/* Step Content */}
              <div className="transition-all duration-300 transform">
                {renderStepContent()}
              </div>
            </div>
            
            {/* Navigation */}
            <div className="flex justify-between mt-3">
              <button
                type="button"
                onClick={handlePrevStep}
                className={`px-3 py-2 rounded-lg font-medium transition-all duration-200 transform hover:scale-105
                  ${isFirstStep()
                    ? 'opacity-50 cursor-not-allowed bg-gray-200'
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700 hover:shadow-md'
                  }`}
                disabled={isFirstStep()}
              >
                ← Précédent
              </button>
              <button
                type="submit"
                disabled={isSubmitting || apiLoading}
                className={`px-3 py-2 bg-gradient-to-r from-teal-600 to-teal-700 text-white rounded-lg font-medium
                  hover:from-teal-700 hover:to-teal-800 transition-all duration-200 transform hover:scale-105
                  focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 hover:shadow-lg
                  disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none`}
              >
                <span className="flex items-center">
                  {isSubmitting ? 'Création en cours...' : 'Créer le Future'}
                </span>
              </button>
            </div>
          </form>
        ) : (
          <div>
            <div className="bg-white rounded-lg shadow p-2 relative overflow-hidden">
              {/* Section Header */}
              <div className="flex items-center mb-2">
                <div className="p-1.5 bg-teal-100 rounded mr-2">
                  {getCurrentSection().icon}
                </div>
                <div>
                  <h2 className="text-lg font-bold text-gray-800">
                    {getCurrentSection().title}
                  </h2>
                  <p className="text-xs text-gray-600">
                    {getCurrentSection().description}
                  </p>
                </div>
              </div>
              
              {/* Step Content */}
              <div className="transition-all duration-300 transform">
                {renderStepContent()}
              </div>
            </div>
            
            {/* Navigation */}
            <div className="flex justify-between mt-6">
              <button
                type="button"
                onClick={handlePrevStep}
                className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                  isFirstStep()
                    ? 'opacity-50 cursor-not-allowed bg-gray-200'
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700'
                }`}
                disabled={isFirstStep()}
              >
                ← Précédent
              </button>
              <button
                type="button"
                onClick={handleNextStep}
                className="px-6 py-3 bg-teal-600 text-white rounded-lg font-medium
                         hover:bg-teal-700 transition-all duration-200"
              >
                Suivant →
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FutureCreationForm;
